import React,{useState} from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import WorkspacePremiumIcon from '@mui/icons-material/WorkspacePremium';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

function Spin() {
  const [num, setNum] = useState(0);
  const [open, setOpen] = useState(false);
  const handleOpen = () => {
    setOpen(true);
    setNum(randomNumberInRange(1, 5));
  }

  const handleClose = () => setOpen(false);

  function randomNumberInRange(min, max) {
    // üëáÔ∏è get number between min (inclusive) and max (inclusive)
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }


  return (
      <>
    <div style={{padding:"20px"}}>
      <h1>Spin</h1>
      <p style={{marginTop:"20px",fontSize:"19px",color:"grey"}}> The amount you earn and the number of spins depends on your rank</p>
    </div>
    <Grid container style={{padding:"20px"}}>
        <Grid lg={4} md={6} xs={12}>

        <Card variant="outlined">
     <CardContent>
         <div style={{display:"flex"}}>
         <p>Your Rank is:</p>
         <span style={{marginLeft:"10px"}}> Diamond</span>
         </div>

         <div style={{display:"flex"}}>
         <p>Balance:</p>
         <span style={{marginLeft:"10px"}}> R105</span>
         </div>

         <div style={{marginTop:"20px",display:"flex",alignItems:"center"}} >
         <WorkspacePremiumIcon />
         <p>Each spin earning range from <b>R30 to R80</b> </p>
         </div>
         <div style={{display:"flex",alignItems:"center"}}>
         <WorkspacePremiumIcon />
             <p>7 remaining spin </p>
         </div>
         <div style={{display:"flex",alignItems:"center"}} >
         <WorkspacePremiumIcon />
             <p>8x spin daily </p>
         </div>






   </CardContent>
   <CardActions>
     <Button onClick={handleOpen} size="small">Spin</Button>
   </CardActions>
   </Card>

        </Grid>


    </Grid>

    <Modal
  open={open}
  onClose={handleClose}
  aria-labelledby="modal-modal-title"
  aria-describedby="modal-modal-description"
>
  <Box sx={style}>
    <Typography id="modal-modal-title" variant="h6" component="h2">
      Text in a modal
    </Typography>
    <Typography id="modal-modal-description" sx={{ mt: 2 }}>
      Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
    </Typography>
  </Box>
</Modal>

   </>
  )
}

export default Spin
